import React, { useState } from 'react'
import Main from './Main'

const Header = () => {
  const [activity, setActivity] = useState('')
  const [todos, setTodos] = useState([])

  const generateId = () => {
    return Date.now();
  }

  const addTodoHandler = (e) => {
    e.preventDefault()
    setTodos([
      ...todos,
      {
        id : generateId(),
        activity
      }
    ])
  }

  return (
    <header>
        {/* <img src='../assets/images/homepage-sass.png' alt='test' /> */}
        <h1>Things to do</h1>
        <form
          onSubmit={addTodoHandler}
          id="new-task-form"
        >
          <input
            id="new-task-input"
            type='text'
            placeholder="input task here"
            value={activity}
            autocomplete="off"
            required
          />
          <button 
            id="new-task-submit" 
            type='submit'
          >
            Submit
          </button>
        </form>
    </header>
  )
}

export default Header
