import React from 'react'

const Header = ({ saveTodoHandler, activity, setActivity, edit, message, cancleTodoHandler }) => {
  return (

      <header>
          {/* <img src='../assets/images/homepage-sass.png' alt='test' /> */}
          <h1>Things to do</h1>
          {message && 
              <div style={{color:'red'}}>
                  {message}
              </div>
          }
          <form
            onSubmit={saveTodoHandler}
            id="new-task-form"
          >
            <input
              id="new-task-input"
              type='text'
              placeholder="input task here"
              value={activity}
              onChange={(e) => {setActivity(e.target.value)}}
              autocomplete="off"
              required
            />
            <button 
              id="new-task-submit" 
              type='submit'
            >
            {edit.id && 
              <button 
                id="new-task-submit" 
                onClick={cancleTodoHandler}
              >
                Cancel
              </button>
            }
              {edit.id ? 'Save Changes' : 'Add'}
            </button>
          </form>
      </header>

  )
}

export default Header
